    _updateDelay: 600,
    _infoBoxHolder: null,
    _markerHolder: null,
    _processingMarkers: false,
    _changes: {},
    _changeSeq: 0,
    _users: {},
    _descCallbacks: {},
    _changeTypes: {},
    _colours: ['green', 'blue', 'red', 'yellow', 'orange', 'purple', 'pink'],
    _userColours: {},
    _rejectCallbacks: {},
    _approveCallbacks: {},
    _comments: {},
    _batchChangeid: null,
    _actionTypes: {
        remove: 'Deletes',
        insert: 'Inserts',
        format: 'Format',
        comment: 'Comments'
    },
    _actionTypeDisplayStates: {
        insert: false,
        remove: true,
        format: true,
        comment: true
    },
